import OpenAI from "openai";
import type { Product } from "@shared/schema";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

export interface ChatMessage {
  role: 'user' | 'assistant' | 'system';
  content: string;
  timestamp?: string;
}

export interface ChatResponse {
  message: string;
  products?: Product[];
  searchResults?: Product[];
  action?: 'search' | 'recommend' | 'info' | 'order_help';
}

export class AIChatbot {
  private systemPrompt = `‡¶Ü‡¶™‡¶®‡¶ø Trynex Lifestyle ‡¶è‡¶∞ AI ‡¶∏‡¶π‡¶æ‡¶Ø‡¶º‡¶ï‡•§ ‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ ‡¶®‡¶æ‡¶Æ "‡¶ü‡ßç‡¶∞‡¶æ‡¶á‡¶®‡ßá‡¶ï‡ßç‡¶∏ ‡¶è‡¶Ü‡¶á"‡•§ ‡¶Ü‡¶™‡¶®‡¶ø ‡¶è‡¶ï‡¶ü‡¶ø ‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ ‡¶á-‡¶ï‡¶Æ‡¶æ‡¶∞‡ßç‡¶∏ ‡¶ì‡¶Ø‡¶º‡ßá‡¶¨‡¶∏‡¶æ‡¶á‡¶ü‡ßá‡¶∞ ‡¶ó‡ßç‡¶∞‡¶æ‡¶π‡¶ï ‡¶∏‡ßá‡¶¨‡¶æ ‡¶™‡ßç‡¶∞‡¶§‡¶ø‡¶®‡¶ø‡¶ß‡¶ø‡•§

‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ ‡¶ï‡¶æ‡¶ú:
1. ‡¶ó‡ßç‡¶∞‡¶æ‡¶π‡¶ï‡¶¶‡ßá‡¶∞ ‡¶™‡¶£‡ßç‡¶Ø ‡¶ñ‡ßÅ‡¶Å‡¶ú‡ßá ‡¶¶‡¶ø‡¶§‡ßá ‡¶∏‡¶æ‡¶π‡¶æ‡¶Ø‡ßç‡¶Ø ‡¶ï‡¶∞‡¶æ
2. ‡¶™‡¶£‡ßç‡¶Ø‡ßá‡¶∞ ‡¶¨‡¶ø‡¶∏‡ßç‡¶§‡¶æ‡¶∞‡¶ø‡¶§ ‡¶§‡¶•‡ßç‡¶Ø ‡¶¶‡ßá‡¶ì‡¶Ø‡¶º‡¶æ
3. ‡¶Ö‡¶∞‡ßç‡¶°‡¶æ‡¶∞ ‡¶™‡ßç‡¶∞‡¶ï‡ßç‡¶∞‡¶ø‡¶Ø‡¶º‡¶æ ‡¶∏‡¶Æ‡ßç‡¶™‡¶∞‡ßç‡¶ï‡ßá ‡¶∏‡¶æ‡¶π‡¶æ‡¶Ø‡ßç‡¶Ø ‡¶ï‡¶∞‡¶æ
4. ‡¶°‡ßá‡¶≤‡¶ø‡¶≠‡¶æ‡¶∞‡¶ø ‡¶ì ‡¶™‡ßá‡¶Æ‡ßá‡¶®‡ßç‡¶ü ‡¶∏‡¶Æ‡ßç‡¶™‡¶∞‡ßç‡¶ï‡ßá ‡¶§‡¶•‡ßç‡¶Ø ‡¶¶‡ßá‡¶ì‡¶Ø‡¶º‡¶æ
5. ‡¶™‡¶£‡ßç‡¶Ø‡ßá‡¶∞ ‡¶∏‡ßÅ‡¶™‡¶æ‡¶∞‡¶ø‡¶∂ ‡¶ï‡¶∞‡¶æ

‡¶ó‡ßÅ‡¶∞‡ßÅ‡¶§‡ßç‡¶¨‡¶™‡ßÇ‡¶∞‡ßç‡¶£ ‡¶§‡¶•‡ßç‡¶Ø:
- ‡¶ï‡ßã‡¶Æ‡ßç‡¶™‡¶æ‡¶®‡¶ø: Trynex Lifestyle
- ‡¶°‡ßá‡¶≤‡¶ø‡¶≠‡¶æ‡¶∞‡¶ø ‡¶ö‡¶æ‡¶∞‡ßç‡¶ú: ‡¶¢‡¶æ‡¶ï‡¶æ‡¶Ø‡¶º ‡ßÆ‡ß¶ ‡¶ü‡¶æ‡¶ï‡¶æ, ‡¶¢‡¶æ‡¶ï‡¶æ‡¶∞ ‡¶¨‡¶æ‡¶á‡¶∞‡ßá ‡ßÆ‡ß¶-‡ßß‡ß®‡ß¶ ‡¶ü‡¶æ‡¶ï‡¶æ
- ‡¶™‡ßá‡¶Æ‡ßá‡¶®‡ßç‡¶ü: bKash, Nagad, ‡¶ï‡ßç‡¶Ø‡¶æ‡¶∂ ‡¶Ö‡¶® ‡¶°‡ßá‡¶≤‡¶ø‡¶≠‡¶æ‡¶∞‡¶ø
- ‡¶π‡ßã‡¶Ø‡¶º‡¶æ‡¶ü‡¶∏‡¶Ö‡ßç‡¶Ø‡¶æ‡¶™: +8801648534981
- ‡¶°‡ßá‡¶≤‡¶ø‡¶≠‡¶æ‡¶∞‡¶ø ‡¶∏‡¶Æ‡¶Ø‡¶º: ‡¶¢‡¶æ‡¶ï‡¶æ‡¶Ø‡¶º ‡ßß-‡ß® ‡¶¶‡¶ø‡¶®, ‡¶¨‡¶æ‡¶á‡¶∞‡ßá ‡ß®-‡ß© ‡¶¶‡¶ø‡¶®

‡¶∏‡¶¨‡¶∏‡¶Æ‡¶Ø‡¶º ‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ‡¶Ø‡¶º ‡¶â‡¶§‡ßç‡¶§‡¶∞ ‡¶¶‡¶ø‡¶®‡•§ ‡¶¨‡¶ø‡¶®‡¶Ø‡¶º‡ßÄ ‡¶è‡¶¨‡¶Ç ‡¶∏‡¶π‡¶æ‡¶Ø‡¶º‡¶ï ‡¶π‡¶®‡•§ ‡¶™‡¶£‡ßç‡¶Ø ‡¶ñ‡ßã‡¶Å‡¶ú‡¶æ‡¶∞ ‡¶ú‡¶®‡ßç‡¶Ø ‡¶Ö‡¶®‡ßÅ‡¶∞‡ßã‡¶ß ‡¶ï‡¶∞‡¶≤‡ßá JSON ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü‡ßá response ‡¶¶‡¶ø‡¶®‡•§`;

  async generateResponse(
    message: string, 
    products: Product[] = [], 
    chatHistory: ChatMessage[] = []
  ): Promise<ChatResponse> {
    try {
      // Detect if user is searching for products
      const isProductSearch = this.detectProductSearch(message);

      if (isProductSearch) {
        const searchResults = this.searchProducts(message, products);
        const response = await this.generateSearchResponse(message, searchResults);

        return {
          message: response,
          searchResults,
          action: 'search'
        };
      }

      // Regular conversation
      const conversation = [
        { role: 'system' as const, content: this.systemPrompt },
        ...chatHistory.slice(-6), // Keep last 6 messages for context
        { role: 'user' as const, content: message }
      ];

      const completion = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: conversation,
        max_tokens: 500,
        temperature: 0.7,
        response_format: { type: "json_object" },
      });

      const result = JSON.parse(completion.choices[0].message.content || '{"message": "‡¶¶‡ßÅ‡¶É‡¶ñ‡¶ø‡¶§, ‡¶Ü‡¶Æ‡¶ø ‡¶è‡¶á ‡¶Æ‡ßÅ‡¶π‡ßÇ‡¶∞‡ßç‡¶§‡ßá ‡¶â‡¶§‡ßç‡¶§‡¶∞ ‡¶¶‡¶ø‡¶§‡ßá ‡¶™‡¶æ‡¶∞‡¶õ‡¶ø ‡¶®‡¶æ‡•§"}');

      return {
        message: result.message || "‡¶ï‡ßÄ‡¶≠‡¶æ‡¶¨‡ßá ‡¶∏‡¶æ‡¶π‡¶æ‡¶Ø‡ßç‡¶Ø ‡¶ï‡¶∞‡¶§‡ßá ‡¶™‡¶æ‡¶∞‡¶ø?",
        action: result.action || 'info'
      };

    } catch (error) {
      console.error('AI Chatbot Error:', error);
      return {
        message: "‡¶¶‡ßÅ‡¶É‡¶ñ‡¶ø‡¶§, ‡¶è‡¶ï‡¶ü‡¶ø ‡¶∏‡¶Æ‡¶∏‡ßç‡¶Ø‡¶æ ‡¶π‡¶Ø‡¶º‡ßá‡¶õ‡ßá‡•§ ‡¶Ü‡¶¨‡¶æ‡¶∞ ‡¶ö‡ßá‡¶∑‡ßç‡¶ü‡¶æ ‡¶ï‡¶∞‡ßÅ‡¶® ‡¶¨‡¶æ ‡¶π‡ßã‡¶Ø‡¶º‡¶æ‡¶ü‡¶∏‡¶Ö‡ßç‡¶Ø‡¶æ‡¶™‡ßá (+8801648534981) ‡¶Ø‡ßã‡¶ó‡¶æ‡¶Ø‡ßã‡¶ó ‡¶ï‡¶∞‡ßÅ‡¶®‡•§",
        action: 'info'
      };
    }
  }

  private detectProductSearch(message: string): boolean {
    const searchKeywords = [
      '‡¶ñ‡ßÅ‡¶Å‡¶ú‡¶õ‡¶ø', '‡¶ö‡¶æ‡¶á', '‡¶¶‡ßá‡¶ñ‡¶æ‡¶ì', '‡¶Ü‡¶õ‡ßá ‡¶ï‡¶ø', '‡¶™‡¶£‡ßç‡¶Ø', '‡¶™‡ßç‡¶∞‡ßã‡¶°‡¶æ‡¶ï‡ßç‡¶ü',
      '‡¶ó‡¶ø‡¶´‡¶ü', '‡¶â‡¶™‡¶π‡¶æ‡¶∞', '‡¶ï‡¶ø‡¶®‡¶§‡ßá', 'search', 'find', 'show', 'product'
    ];

    const lowerMessage = message.toLowerCase();
    return searchKeywords.some(keyword => lowerMessage.includes(keyword));
  }

  private searchProducts(query: string, products: Product[]): Product[] {
    const searchTerms = query.toLowerCase().split(' ');

    return products.filter(product => {
      const searchableText = `${product.name} ${product.description} ${product.category}`.toLowerCase();
      return searchTerms.some(term => searchableText.includes(term));
    }).slice(0, 5); // Return top 5 results
  }

  private async generateSearchResponse(query: string, products: Product[]): Promise<string> {
    if (products.length === 0) {
      return "‡¶¶‡ßÅ‡¶É‡¶ñ‡¶ø‡¶§, ‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ ‡¶ñ‡ßã‡¶Å‡¶ú‡¶æ ‡¶™‡¶£‡ßç‡¶Ø ‡¶ñ‡ßÅ‡¶Å‡¶ú‡ßá ‡¶™‡¶æ‡¶á‡¶®‡¶ø‡•§ ‡¶Ö‡¶®‡ßç‡¶Ø ‡¶ï‡ßã‡¶®‡ßã ‡¶ï‡ßÄ‡¶ì‡¶Ø‡¶º‡¶æ‡¶∞‡ßç‡¶° ‡¶¶‡¶ø‡¶Ø‡¶º‡ßá ‡¶ö‡ßá‡¶∑‡ßç‡¶ü‡¶æ ‡¶ï‡¶∞‡ßÅ‡¶® ‡¶¨‡¶æ ‡¶π‡ßã‡¶Ø‡¶º‡¶æ‡¶ü‡¶∏‡¶Ö‡ßç‡¶Ø‡¶æ‡¶™‡ßá (+8801648534981) ‡¶Ø‡ßã‡¶ó‡¶æ‡¶Ø‡ßã‡¶ó ‡¶ï‡¶∞‡ßÅ‡¶®‡•§";
    }

    const productList = products.map(p => `‚Ä¢ ${p.name} - ${p.price}‡ß≥`).join('\n');

    return `‡¶Ü‡¶™‡¶®‡¶æ‡¶∞ ‡¶ú‡¶®‡ßç‡¶Ø ${products.length}‡¶ü‡¶ø ‡¶™‡¶£‡ßç‡¶Ø ‡¶™‡ßá‡¶Ø‡¶º‡ßá‡¶õ‡¶ø:\n\n${productList}\n\n‡¶¨‡¶ø‡¶∏‡ßç‡¶§‡¶æ‡¶∞‡¶ø‡¶§ ‡¶¶‡ßá‡¶ñ‡¶§‡ßá ‡¶™‡¶£‡ßç‡¶Ø‡ßá‡¶∞ ‡¶â‡¶™‡¶∞ ‡¶ï‡ßç‡¶≤‡¶ø‡¶ï ‡¶ï‡¶∞‡ßÅ‡¶® ‡¶¨‡¶æ ‡¶Ü‡¶∞‡ßã ‡¶ú‡¶æ‡¶®‡¶§‡ßá ‡¶ö‡¶æ‡¶á‡¶≤‡ßá ‡¶ú‡¶ø‡¶ú‡ßç‡¶û‡¶æ‡¶∏‡¶æ ‡¶ï‡¶∞‡ßÅ‡¶®‡•§`;
  }

  async getSmartRecommendations(userInterests: string[], products: Product[]): Promise<Product[]> {
    try {
      const prompt = `Based on these user interests: ${userInterests.join(', ')}, recommend the best products from this list. Return product IDs only as JSON array.`;

      const completion = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          { role: 'system', content: 'You are a product recommendation AI. Analyze user interests and recommend relevant products.' },
          { role: 'user', content: prompt + '\n\nProducts: ' + JSON.stringify(products.map(p => ({ id: p.id, name: p.name, category: p.category }))) }
        ],
        max_tokens: 200,
        response_format: { type: "json_object" }
      });

      const result = JSON.parse(completion.choices[0].message.content || '{"recommendations": []}');
      const recommendedIds = result.recommendations || [];

      return products.filter(p => recommendedIds.includes(p.id)).slice(0, 4);
    } catch (error) {
      console.error('Smart Recommendations Error:', error);
      return products.slice(0, 4); // Fallback to first 4 products
    }
  }
}

