      const order = await storage.createOrder(orderData);

      res.status(201).json({
        success: true,
        tracking_id: order.tracking_id,
        order_id: order.id,
        message: '‡¶Ö‡¶∞‡ßç‡¶°‡¶æ‡¶∞ ‡¶∏‡¶´‡¶≤‡¶≠‡¶æ‡¶¨‡ßá ‡¶§‡ßà‡¶∞‡¶ø ‡¶π‡¶Ø‡¶º‡ßá‡¶õ‡ßá'
      });
