      -webkit-overflow-scrolling: touch;
      scrollbar-width: none;
      -ms-overflow-style: none;
    }

    .mobile-tabs-scroll::-webkit-scrollbar {
      display: none;
    }

    /* Ensure cards don't break layout on small screens */
    .admin-stats-card {
      min-width: 0;
      flex: 1;
    }
  }
}

@layer utilities {
  .font-bengali {
    font-family: 'Hind Siliguri', 'Inter', sans-serif;
  }
}

/* Custom animations */
@keyframes bounce {
  0%, 20%, 53%, 80%, 100% {
    transform: translateY(0);
  }
  40%, 43% {
    transform: translateY(-8px);
  }
  70% {
    transform: translateY(-4px);
  }
  90% {
    transform: translateY(-2px);
  }
}

.animate-bounce {
  animation: bounce 2s infinite;
}

/* Smooth transitions */
.transition-colors {
  transition: color 0.15s ease-in-out, background-color 0.15s ease-in-out, border-color 0.15s ease-in-out;
}

.transition-transform {
  transition: transform 0.15s ease-in-out;
}

.transition-shadow {
  transition: box-shadow 0.15s ease-in-out;
}

/* Hover effects */
.hover-scale:hover {
  transform: scale(1.05);
}

/* Cart animation */
.cart-slide-enter {
  transform: translateX(100%);
}

.cart-slide-enter-active {
  transform: translateX(0);
  transition: transform 0.3s ease-out;
}

.cart-slide-exit {
  transform: translateX(0);
}

.cart-slide-exit-active {
  transform: translateX(100%);
  transition: transform 0.3s ease-in;
}

/* Enhanced Loading Animations */
@keyframes shimmer {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

@keyframes slideInUp {
  from { 
    opacity: 0; 
    transform: translateY(30px) scale(0.95); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0) scale(1); 
  }
}

@keyframes pulseGlow {
  0%, 100% { 
    box-shadow: 0 0 0 0 rgba(34, 197, 94, 0.4);
    transform: scale(1);
  }
  50% { 
    box-shadow: 0 0 0 10px rgba(34, 197, 94, 0);
    transform: scale(1.02);
  }
}

@keyframes loadingDots {
  0%, 20% { opacity: 0; }
  50% { opacity: 1; }
  100% { opacity: 0; }
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.animate-shimmer {
  animation: shimmer 2s infinite linear;
}

.animate-slideInUp {
  animation: slideInUp 0.6s ease-out forwards;
}

.animate-pulseGlow {
  animation: pulseGlow 2s infinite;
}

.animate-loadingDots {
  animation: loadingDots 1.5s infinite;
}

.animate-fadeIn {
  animation: fadeIn 0.8s ease-out forwards;
}

/* Enhanced skeleton loading styles */
.loading-skeleton {
  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
}

.loading-skeleton-fast {
  background: linear-gradient(90deg, #f8f9fa 25%, #e9ecef 50%, #f8f9fa 75%);
  background-size: 200% 100%;
  animation: shimmer 1s infinite;
}

/* Premium loading effects */
.premium-loading-card {
  background: linear-gradient(145deg, #ffffff 0%, #f8fafc 100%);
  border: 1px solid rgba(0, 0, 0, 0.05);
  box-shadow: 
    0 4px 6px -1px rgba(0, 0, 0, 0.1),
    0 2px 4px -1px rgba(0, 0, 0, 0.06),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.premium-loading-card:hover {
  transform: translateY(-2px) scale(1.02);
  box-shadow: 
    0 10px 15px -3px rgba(0, 0, 0, 0.1),
    0 4px 6px -2px rgba(0, 0, 0, 0.05);
}

