  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  // Search API call
  const { data: searchResults, isLoading, error } = useQuery<SearchResult>({
    queryKey: ["/api/search", debouncedQuery],
