          setTimeout(() => {
            const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;

            console.log('üöÄ Page Performance Metrics:');
            console.log(`‚Ä¢ DOM Load: ${navigation.domContentLoadedEventEnd - navigation.navigationStart}ms`);
