            // Track if we're meeting performance goals
            const totalLoad = navigation.loadEventEnd - navigation.navigationStart;
            if (totalLoad < 1000) {
              console.log('‚úÖ Excellent load time (<1s)');
            } else if (totalLoad < 2000) {
              console.log('‚úÖ Good load time (<2s)');
            } else {
              console.log('‚ö†Ô∏è Slow load time (>2s) - optimization needed');
            }
          }, 100);
        });
      }
    };

    measureWebVitals();
  }, []);

  return null;
}

