    // Show regular popup offers
    else if (popupOffer && !hasShown && !shouldShowFreeDelivery) {
      const timer = setTimeout(() => {
        setIsOpen(true);
        setHasShown(true);
      }, popupOffer.popup_delay || 3000);

      return () => clearTimeout(timer);
