  }, [product.id, onToggleWishlist]);

  return (
    <Card 
      className="group hover:shadow-lg transition-all duration-300 border-0 bg-white"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <CardContent className="p-0">
