client/src/hooks/useInfiniteProducts.tsx.content:
import { useState, useEffect, useCallback } from 'react';
import { useQuery } from '@tanstack/react-query';
import type { Product } from '@shared/schema';

interface UseInfiniteProductsOptions {
  category?: string;
  searchTerm?: string;
  sortBy?: string;
  priceRange?: string;
  pageSize?: number;
  enabled?: boolean;
}

interface UseInfiniteProductsReturn {
  products: Product[];
  isLoading: boolean;
  isLoadingMore: boolean;
  hasNextPage: boolean;
  loadMore: () => void;
  error: Error | null;
  totalCount: number;
  currentPage: number;
}

export const useInfiniteProducts = (options: UseInfiniteProductsOptions = {}): UseInfiniteProductsReturn => {
  const {
    category = 'all',
    searchTerm = '',
    sortBy = 'newest',
    priceRange = 'all',
    pageSize = 12,
    enabled = true
  } = options;

  const [currentPage, setCurrentPage] = useState(1);
  const [allProducts, setAllProducts] = useState<Product[]>([]);
  const [isLoadingMore, setIsLoadingMore] = useState(false);

  // Query for all products with optimized caching
  const { data: products = [], isLoading, error } = useQuery<Product[]>({
    queryKey: ['/api/products'],
    staleTime: 1000 * 60 * 5, // 5 minutes
    gcTime: 1000 * 60 * 15, // 15 minutes
    refetchOnWindowFocus: false,
    enabled,
    retry: 1,
    placeholderData: []
  });

  // Filter and sort products
  const processedProducts = products
    .filter((product) => {
      // Category filter
      if (category !== 'all' && product.category !== category) return false;

      // Search filter
      if (searchTerm && !product.name.toLowerCase().includes(searchTerm.toLowerCase())) return false;

      // Price range filter
      if (priceRange !== 'all') {
        const price = Number(product.price);
        switch (priceRange) {
          case 'under-500':
            if (price >= 500) return false;
            break;
          case '500-1000':
            if (price < 500 || price > 1000) return false;
            break;
          case '1000-2000':
            if (price < 1000 || price > 2000) return false;
            break;
          case 'over-2000':
            if (price <= 2000) return false;
            break;
        }
      }

      return true;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return Number(a.price) - Number(b.price);
        case 'price-high':
          return Number(b.price) - Number(a.price);
        case 'name':
          return a.name.localeCompare(b.name);
        case 'newest':
        default:
          return new Date(b.created_at || '').getTime() - new Date(a.created_at || '').getTime();
      }
    });

  // Update displayed products when filters change
  useEffect(() => {
    setCurrentPage(1);
    setAllProducts(processedProducts.slice(0, pageSize));
  }, [category, searchTerm, sortBy, priceRange, pageSize, products]);

  // Load more function
  const loadMore = useCallback(() => {
    if (isLoadingMore || !hasNextPage) return;

    setIsLoadingMore(true);

    // Simulate loading delay for better UX
    setTimeout(() => {
      const nextPage = currentPage + 1;
      const startIndex = (nextPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const newProducts = processedProducts.slice(startIndex, endIndex);

      setAllProducts(prev => [...prev, ...newProducts]);
      setCurrentPage(nextPage);
      setIsLoadingMore(false);
    }, 300);
  }, [currentPage, pageSize, processedProducts, isLoadingMore]);

  // Calculate if there's a next page
  const hasNextPage = allProducts.length < processedProducts.length;

  return {
    products: allProducts,
    isLoading,
    isLoadingMore,
    hasNextPage,
    loadMore,
    error: error as Error | null,
    totalCount: processedProducts.length,
    currentPage
  };
};

client/src/hooks/useOptimizedProducts.ts.content:
import { useState, useEffect, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import type { Product } from "@shared/schema";

interface UseOptimizedProductsProps {
  category?: string;
  searchTerm?: string;
  sortBy?: 'name' | 'price' | 'created_at';
  sortOrder?: 'asc' | 'desc';
  pageSize?: number;
}

export function useOptimizedProducts({
  category,
  searchTerm = '',
  sortBy = 'created_at',
  sortOrder = 'desc',
  pageSize = 16
}: UseOptimizedProductsProps = {}) {
  const [currentPage, setCurrentPage] = useState(1);

  // Ultra-fast products with browser caching
  const { data: allProducts = [], isLoading, error } = useQuery({
    queryKey: ['products-ultra-fast', category],
    queryFn: async () => {
      const CACHE_KEY = `products-cache-${category || 'all'}`;
      const CACHE_DURATION = 365 * 24 * 60 * 60 * 1000; // 1 year

      // Try browser cache first
      try {
        const cached = localStorage.getItem(CACHE_KEY);
        if (cached) {
          const { data, timestamp } = JSON.parse(cached);
          if (Date.now() - timestamp < CACHE_DURATION) {
            console.log('‚ö° Products loaded from browser cache');
            return data;
          }
        }
      } catch (e) {}

      // Fetch with aggressive optimization
      const url = category ? `/api/products?category=${category}` : '/api/products';
      const response = await fetch(url, {
        headers: {
          'Cache-Control': 'max-age=31536000', // 1 year
          'Accept': 'application/json'
        }
      });

      if (!response.ok) throw new Error('Network error');

      const data = await response.json();

      // Cache immediately
      try {
        localStorage.setItem(CACHE_KEY, JSON.stringify({
          data,
          timestamp: Date.now()
        }));
      } catch (e) {}

      console.log('üöÄ Products fetched and cached');
      return data;
    },
    staleTime: Infinity, // Never consider stale
    gcTime: Infinity, // Keep forever
    refetchOnWindowFocus: false,
    refetchOnReconnect: false,
    retry: 1
  });

  // Memoized filtered and sorted products
  const filteredProducts = useMemo(() => {
    let filtered = [...allProducts] as Product[];

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter((product: Product) =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (product.description || '').toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Sort products
    filtered.sort((a: Product, b: Product) => {
      let comparison = 0;

      switch (sortBy) {
        case 'name':
          comparison = a.name.localeCompare(b.name);
          break;
        case 'price':
          comparison = parseFloat(a.price.toString()) - parseFloat(b.price.toString());
          break;
        case 'created_at':
          const dateA = a.created_at ? new Date(a.created_at).getTime() : 0;
          const dateB = b.created_at ? new Date(b.created_at).getTime() : 0;
          comparison = dateA - dateB;
          break;
        default:
          comparison = 0;
      }

      return sortOrder === 'desc' ? -comparison : comparison;
    });

    return filtered;
  }, [allProducts, searchTerm, sortBy, sortOrder]);

  // Paginated products
  const paginatedProducts = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    return (filteredProducts as Product[]).slice(startIndex, endIndex);
  }, [filteredProducts, currentPage, pageSize]);

  // Total pages
  const totalPages = Math.ceil((filteredProducts as Product[]).length / pageSize);

  // Reset page when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [category, searchTerm, sortBy, sortOrder]);

  return {
    products: paginatedProducts,
    allProducts: filteredProducts,
    isLoading,
    error,
    currentPage,
    totalPages,
    totalProducts: (filteredProducts as Product[]).length,
    setCurrentPage,
    hasNextPage: currentPage < totalPages,
    hasPreviousPage: currentPage > 1,
  };
}

