      case 'shipped':
        return '‡¶™‡¶æ‡¶†‡¶æ‡¶®‡ßã ‡¶π‡¶Ø‡¶º‡ßá‡¶õ‡ßá';
      case 'delivered':
        return '‡¶°‡ßá‡¶≤‡¶ø‡¶≠‡¶æ‡¶∞';
      case 'cancelled':
        return '‡¶¨‡¶æ‡¶§‡¶ø‡¶≤';
